mapper.update:
UPDATE documentinfo
SET isTop = #{istop,jdbcType=SMALLINT}
WHERE GUID = #{guid,jdbcType=VARCHAR}

UPDATE documentinfo
SET isCream = #{iscream,jdbcType=SMALLINT}
WHERE GUID = #{guid,jdbcType=VARCHAR}

UPDATE documentinfo
SET status = #{status,jdbcType=SMALLINT}
WHERE GUID = #{guid,jdbcType=VARCHAR}

		UPDATE documentinfo
		
		columnGuid = #{columnguid,jdbcType=VARCHAR},
 		columnName = #{columnname,jdbcType=VARCHAR}
 		
		WHERE GUID = #{guid,jdbcType=VARCHAR}

mapper.select:
		SELECT
		
		FROM documentinfo
		
			
				AND ${wheresql.advSql}
			
				AND status = '0'
			
				AND columnGuid = #{wheresql.columnguid}
			
		

		
			ORDER BY ${ordersql}
		
		
			ORDER by isTop desc, createTime desc

SELECT count(*) FROM documentinfo
WHERE date_format(createTime,'%Y-%m-%d') = #{time,jdbcType=VARCHAR}
AND status = '0'
