mapper.select:
		SELECT
		
		FROM MemberInfo
		
			
				AND ${wheresql.advSql}
			
		

		
			ORDER BY ${ordersql}
		
		
			ORDER by createTime desc

SELECT

FROM MemberInfo

	
		AND cardNumber = #{cardnumber,jdbcType=VARCHAR}
	
	
		 AND guid <> #{withoutguid,jdbcType=VARCHAR}

mapper.update:
update memberinfo
set balance = #{balance,jdbcType=DECIMAL}
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set status = #{status,jdbcType=SMALLINT},
balance = #{balance,jdbcType=DECIMAL}
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set balance = #{balance,jdbcType=DECIMAL}
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set status = #{status,jdbcType=SMALLINT},
activeTime = #{activetime,jdbcType=TIMESTAMP},
expireTime = #{expiretime,jdbcType=TIMESTAMP}
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set expireTime = #{expiretime,jdbcType=TIMESTAMP},
balance = #{balance,jdbcType=DECIMAL}
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set status = #{status,jdbcType=SMALLINT},
expireTime = #{expiretime,jdbcType=TIMESTAMP},
points = #{points,jdbcType=INTEGER},
balance = #{balance,jdbcType=DECIMAL}
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set points = #{points,jdbcType=INTEGER}
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set status = '3'
where guid = #{guid,jdbcType=VARCHAR}

update memberinfo
set status = '2',
expireTime = #{expiretime,jdbcType=TIMESTAMP}
